sysauto_script.script:
//Number of NeedIt records to create
	var recordsToMake = 30;

	// Declare and initialize variables used in the script
	var genNIRec;
	var i;
	var tableName = "x_58872_needit_needit";
	var requestTypeArray = ["facilities","hr","legal"];
	var selectRequestType = "";
	var selectWhatNeeded = 3;
	var pickDate = 1;
	// var requestedForArray = [{"name":"Fred Luddy","email":"fred.luddy@example.com"},{"name":"Beth Anglin","email":"beth.anglin@example.com"},{"name":"System Administrator","email":"admin@example.com"}];
	//var requestedForArray = [{"name":"Power Huang(文杰)","email":"max_tseng@gss.com.tw"},{"name":"David Lin(大為)","email":"max_tseng@gss.com.tw"},{"name":"Young Huang(堯昌)","email":"max_tseng@gss.com.tw"}];
	//var requestedForArray = [{"name":"Bruce Chen(陳○雄)","email":"max_tseng@gss.com.tw"},{"name":"Anrita Ho(何○雲)","email":"max_tseng@gss.com.tw"},{"name":"Miranda Liu(劉○雯)","email":"max_tseng@gss.com.tw"}];
//var requestedForArray = [{"name":"Kevin Lin(林○勳)","email":"max_tseng@gss.com.tw"},{"name":"Max Tseng(曾○大)","email":"max_tseng@gss.com.tw"},{"name":"Albert Wang(王○浩)","email":"max_tseng@gss.com.tw"}];
// var requestedForArray = [{"name":"Fred Fang(方○毅)","email":"max_tseng@gss.com.tw"},{"name":"Tim Su(蘇○傑)","email":"max_tseng@gss.com.tw"},{"name":"Miller Kuo(郭○志)","email":"max_tseng@gss.com.tw"}];
// var requestedForArray = [{"name":"Cindy Liao(廖○鈞)","email":"max_tseng@gss.com.tw"},{"name":"I-fan Huang(黃○凡))","email":"max_tseng@gss.com.tw"},{"name":"Irene Chou(周○伶)","email":"max_tseng@gss.com.tw"}];
//var requestedForArray = [{"name":"Kevin Tang(唐○恒)","email":"max_tseng@gss.com.tw"},{"name":"Frank Huang(黃○逸)","email":"max_tseng@gss.com.tw"},{"name":"Frank Huang(黃○逸)","email":"max_tseng@gss.com.tw"}];
//var requestedForArray = [{"name":"Ifan Huang(黃○凡)","email":"max_tseng@gss.com.tw"},{"name":"Howard Hung(洪○昇)","email":"max_tseng@gss.com.tw"},{"name":"George Chiou(邱○貴)","email":"max_tseng@gss.com.tw"}];
